const Me={title:"Are beds just a capatilist",description:"A discovery piece on why we sleep in beds.",tags:"Writing"};class Ie extends ke{constructor(p){super(),Te(this,p,null,ge,_e,{})}}export{Ie as default,Me as metadata};
